import Interactive from 'react-interactjs'

import React, { Component } from 'react';

class InterRct extends Component {

     draggableOptions =() => {
        onmove: event => {
           const target = event.target
         // keep the dragged position in the data-x/data-y attributes
         const x = (parseFloat(target.getAttribute('data-x')) || 0) + event.dx
         const y = (parseFloat(target.getAttribute('data-y')) || 0) + event.dy
   
         // translate the element
         target.style.webkitTransform =
         target.style.transform =
           'translate(' + x + 'px, ' + y + 'px)'
   
         // update the posiion attributes
         target.setAttribute('data-x', x);
         target.setAttribute('data-y', y);
       }
   }

   
    render() {
        return (
            <Interactive draggable draggableOptions={this.draggableOptions}>
            <img src="https://pbs.twimg.com/profile_images/526421493731717120/INda0NaM.png" height={100} width={100}/>
        </Interactive>
        );
    }
}

export default InterRct;
