import React, { Component } from 'react';
import demoImg from "../../assets/ntr5.jpg";
import waterMark from "../../assets/logo1.jpg";

import "./index.css";

const interact = require('interactjs')

class ResizeDrag extends Component {

    componentDidMount() {
        // target elements with the "draggable" class
        var element = document.getElementById('grid-snap')
        var x = 0; var y = 0
        
        interact(element)
          .draggable({
            modifiers: [
              interact.modifiers.snap({
                targets: [
                  interact.createSnapGrid({ x: 30, y: 30 })
                ],
                range: Infinity,
                relativePoints: [ { x: 0, y: 0 } ]
              }),
              interact.modifiers.restrict({
                restriction: element.parentNode,
                elementRect: { top: 0, left: 0, bottom: 0, right: 0 },
                endOnly: true
              })
            ],
            inertia: true
          })
          .on('dragmove', function (event) {
            x += event.dx
            y += event.dy
        
            event.target.style.webkitTransform =
            event.target.style.transform =
                'translate(' + x + 'px, ' + y + 'px)'
          })
        

    }









    render() {
        return (
            <div>
                <h2>Img resizable </h2>



                {/* <div className="resizemove" > 
                       <img src={demoImg} width="120px" className="resize-drag" /></div> */}



<div className="par" style={{ width : "500px" , height: " 200px" , background : "red"}}>

                <p id="grid-snap" > Draggable Element </p>
                
                </div>
            </div>
        );
    }
}

export default ResizeDrag;
