import React, { Component, Fragment } from "react";
import FacebookLogin from "react-facebook-login";
import "./fb.css";
class FacebookLive extends Component {

    constructor() {
        super();
        this.state = {
            userDetails: {}
        }
    }

    responseFacebook = response => {
        console.log(response);
        if (response) {
            this.setState({
                userDetails: response
            })
        }
    };

    render() {

        let user = this.state.userDetails;
        return (
            <div>
                <h4>Facebook Live (Publish video)  </h4>
                <FacebookLogin
                    appId="1853627951564328"
                    autoLoad={true}
                    fields="name,email,picture"
                    callback={this.responseFacebook}
                    cssClass="my-facebook-button-class"
                    icon="fa-facebook"
                />


                <div>
                    <h3>User Details </h3>
                    <hr />
                    {
                        this.state.userDetails ?
                            <div className="card">
                                {/* <img src={user.pricture.data.url} alt="John" style="width:100%" />
                                <h1> {user.name}</h1>
                                <p className="title">{user.email}</p>
                                <p>Harvard University</p> */}
                            </div>

                            : <div> Login to continue... </div>

                    }

                </div>
            </div>
        )
    }
}

export default FacebookLive;
