import React, { Component } from 'react';
import demoImg from "../../assets/ntr5.jpg";
import waterMark from "../../assets/logo1.jpg";

import "./index.css";

const $ = require('interactjs')

class ResizeDrag extends Component {

    componentDidMount(){var imgArray = ["http://www.techjini.com/wp-content/uploads/2007/01/mozilla-firefox-vector-logo.png",
    "https://www.shareicon.net/data/128x128/2017/04/11/883742_search_512x512.png",
    "https://cdn1.iconfinder.com/data/icons/simple-icons/128/apple-128-black.png"];

var l = imgArray.length;

$(function() {
for(var i = 0; i < l; i++) {
$resizedrag = $('<div id="images'+i+'"><img src="'+imgArray[i]+'"></div>');   //creat new <div> with dynamic id
$resizedrag.addClass("resize-drag");             //add resize-drag class to the above created <div>

$(".resize-container").append($resizedrag);    //append created <div> with setting its detailes to parent <div>
}
});

function dragMoveListener (event) {
var target = event.target,
// keep the dragged position in the data-x/data-y attributes
x = (parseFloat(target.getAttribute('data-x')) || 0) + event.dx,
y = (parseFloat(target.getAttribute('data-y')) || 0) + event.dy;

// translate the element
target.style.webkitTransform =
target.style.transform =
'translate(' + x + 'px, ' + y + 'px)';

// update the posiion attributes
target.setAttribute('data-x', x);
target.setAttribute('data-y', y);
}

interact('.resize-drag')
.draggable({
onmove: window.dragMoveListener,
restrict: {
restriction: 'parent',
elementRect: { top: 1, left: 1, bottom: 1, right: 1 }
},
})
.resizable({
// resize from all edges and corners
edges: { left: true, right: true, bottom: true, top: true },

//keep aspectratio
preserveAspectRatio: true,

// keep the edges inside the parent
restrictEdges: {
outer: 'parent',
endOnly: true,
},

// minimum size
restrictSize: {
min: { width: 100, height: 50 },
},

inertia: true,
})

.on('resizemove', function (event) {
var target = event.target,
x = (parseFloat(target.getAttribute('data-x')) || 0),
y = (parseFloat(target.getAttribute('data-y')) || 0);

// update the element's style
target.style.width  = event.rect.width + 'px';
target.style.height = event.rect.height + 'px';

// translate when resizing from top or left edges
x += event.deltaRect.left;
y += event.deltaRect.top;

target.style.webkitTransform = target.style.transform =
'translate(' + x + 'px,' + y + 'px)';

target.setAttribute('data-x', x);
target.setAttribute('data-y', y);
}); }





  
      



    render() {
        return (
            <div>
                <h2>Img resizable </h2>
        
      

                    <div className="resizemove"> 

      
                       <img src={demoImg} width="120px" className="resize-drag" /></div>

                       <hr />

                       <div className="resize-container"></div>

           </div>
           
        );
    }
}

export default ResizeDrag;
