import React, { Component } from 'react';
import demoImg from "../../assets/ntr5.jpg";
import waterMark from "../../assets/logo1.jpg";

import "./index.css";

const interact = require('interactjs')

class ResizeDrag extends Component {

    componentDidMount(){
        const position = { x: 0, y: 0 }

        // create a restrict modifier to prevent dragging an element out of its parent
            const restrictToParent = interact.modifiers.restrict({
                restriction: 'parent',
                elementRect: { left: 0, right: 0, top: 1, bottom: 1 },
            })

            const snap100x100 = interact.modifiers.snap({
                targets: [interact.snappers.grid({ x: 100, y: 100 })],
                relativePoints: [{ x: 0.5, y: 0.5 }],
              })

              
           

        interact('.draggable').draggable({
          listeners: {
            start (event) {
              console.log(event.type, event.target)
              interact(event.target)
              .draggable({
                  // apply the restrict and then the snap modifiers to drag events
                  modifiers: [restrictToParent, snap100x100],
              })
              .on('dragmove', event => console.log(event.pageX, event.pageY))

            },
            move (event) {
              position.x += event.dx
              position.y += event.dy
        
              event.target.style.transform =
                `translate(${position.x}px, ${position.y}px)`
            },
          }
        })


    }




  
      



    render() {
        return (
            <div>
                <h2>Img resizable </h2>
        
      
{/* 
                    <div className="resizemove" > 

      
                       <img src={demoImg} width="120px" className="resize-drag" /></div>

                       <hr /> */}

                       <div style={{ width : "500px" , height : "600px" , background : "green"}}> 

                  

                       <div class="draggable" > Draggable Element </div>
                       </div>

                       </div>
           
        );
    }
}

export default ResizeDrag;
