import React, { Component } from 'react';


import ReactPlayer from 'react-player'
const interact = require('interactjs');
let style={ background :"red" , margin : "20px" , height : "500px" , width : "1000px" };

class VideoEditor extends Component {

    
    
    componentDidMount(){


              // drag the element on page 
              const position = { x: 0, y: 0 }
              interact('.draggable-logo').draggable({
                  listeners: {
                      start(event) {
                          // console.log(event.type, event.target)
                      },
                      move(event) {
                          position.x += event.dx
                          position.y += event.dy
      
                          event.target.style.transform =
                              `translate(${position.x}px, ${position.y}px)`
      
                          console.log(event.type, event.target)
                      },
      
                  }
              })

              //resiz
        interact(".resizable-logo")
        .resizable({
            edges: { left: false, right: true, bottom: true, top: false },
            // modifiers: [
            //   interact.modifiers.restrictEdges({
            //     outer: "parent",
            //     endOnly: true
            //   })
            // ],
            inertia: true,
            modifiers: [
                // keep the edges inside the parent
                // interact.modifiers.restrictEdges({
                //   outer: 'parent'
                // }),

                // minimum size
                interact.modifiers.restrictSize({
                    min: { width: 20, height: 20 }
                })
            ],
        })
        .on("resizemove", function (event) {
            var target = event.target
            console.log(target);
            target.style.width = event.rect.width + "px"
            target.style.height = event.rect.height + "px"
        })
    }

    render() {
        return (
            
            <div> 
            <div style={{ height : "1000px"}}>
                Video editor
                </div>

           
               <div
                style={style} >
                      <div className="draggable-logo" >
                   <ReactPlayer url='https://www.dailymotion.com/video/x5e9eog'
                    playing  
                    className="resizable-logo"
                    
                    />
                    </div>
                 
             </div>


             <br />   <br />   <br />   <br />   <br />   <br />
             </div>
        );
    }
}

export default VideoEditor;
