import React, { Component } from 'react';
import demoImg from "../../assets/ntr5.jpg";
import waterMark from "../../assets/logo1.jpg";

import "./index.css";

const interact = require('interactjs')

class ResizeDrag extends Component {

    componentDidMount(){
        interact('#drag-container')
        .draggable({
          onmove: window.dragMoveListener
        })
      
      function dragMoveListener (event) {
          var target = event.target,
              // keep the dragged position in the data-x/data-y attributes
              x = (parseFloat(target.getAttribute('data-x')) || 0) + event.dx,
              y = (parseFloat(target.getAttribute('data-y')) || 0) + event.dy;
      
          // translate the element
          target.style.webkitTransform =
          target.style.transform =
            'translate(' + x + 'px, ' + y + 'px)';
      
          // update the posiion attributes
          target.setAttribute('data-x', x);
          target.setAttribute('data-y', y);
        }
    }





  
      



    render() {
        return (
            <div>
                <h2>Img resizable </h2>
        
      

                    <div className="resizemove"> 

      
                       <img src={demoImg} width="120px" className="resize-drag" /></div>

                       <hr />

                       <div className="resize-container"></div>

                       </div>
           
        );
    }
}

export default ResizeDrag;
