import React, { Component } from 'react';

class FilterSamples extends Component {

    constructor(props) {
        super(props);


        this.state = {
            filterSamples: [{
                src: "sdf",
                filterName: "normal"
            },
            {
                src: "sdfe",
                filterName: "grayscale"
            }, {
                src: "sdfe",
                filterName: "blur"
            }
                , {
                src: "sdfe",
                filterName: "invert"
            }]
        }
    }

    filterSamples = () => {
        let res = this.state.filterSamples;
        res = res.map((ele, i) => {
            return <img src={ele.src} 
            alt={ele.filterName} 
            width="50px" height="50px"
            onClick={ () => this.applyFilter(ele.filterName)} />
        })

        return res;
    }


    applyFilter = ( filterName ) => {
        this.props.filterEffect( "one" ,"normal");
    }

    render() {
        return (
            <div>
                <h5>Img filters </h5>
                {this.filterSamples()}
            </div>
        );
    }
}

export default FilterSamples;
