import React, { Component, Fragment } from "react";
import FacebookLogin from "react-facebook-login";
import "./fb.css";
class FacebookLive extends Component {

    constructor() {
        super();
        this.state = {
            userDetails: null
        }
    }

    responseFacebook = response => {
        console.log(response);
        if (response) {
            this.setState({
                userDetails: response.accessToken ? response : null
            })
        }
    };

    render() {
        return (
            <div>
                <h4>Facebook Live (Publish video)  </h4>
                <FacebookLogin
                    appId="1853627951564328"
                    autoLoad={true}
                    fields="name,email,picture"
                    callback={this.responseFacebook}
                    cssClass="my-facebook-button-class"
                    icon="fa-facebook"
                />


                <div>

                    {
                        this.state.userDetails ?
                            <div>
                                <h3>User Details </h3>
                                <hr />
                                <div className="card">
                                <img src={this.state.userDetails.picture.data.url} alt="userProfile" style={{width:"100%" }}/>
                                <h1> { this.state.userDetails.name}</h1>
                                <h3>{ this.state.userDetails.email}</h3>
                       
                          
                            </div>
                            </div>
                            : <div> Login to continue... </div>

                    }

                </div>
            </div>
        )
    }
}

export default FacebookLive;
