import React, { Component } from 'react';
import demoImg from "../../assets/ntr1.jpg";
import waterMark from "../../assets/logo1.jpg";

const interact = require('interactjs')

class InteractJS extends Component {

    componentDidMount(){
        interact(".resizable")
        .resizable({
                edges: { left: false, right: true, bottom: true, top: false },
                // modifiers: [
                //   interact.modifiers.restrictEdges({
                //     outer: "parent",
                //     endOnly: true
                //   })
                // ],
                inertia: true
            })
            .on("resizemove", function(event) {
                var target = event.target
                console.log(target);
                target.style.width = event.rect.width + "px"
                target.style.height = event.rect.height + "px"
            })


            // drag 

            let element = document.getElementById('grid-snap')
            let x = 0; let y = 0

            interact(element)
                .draggable({
                    modifiers: [
                    interact.modifiers.snap({
                        targets: [
                        interact.createSnapGrid({ x: 30, y: 30 })
                        ],
                        range: Infinity,
                        relativePoints: [ { x: 0, y: 0 } ]
                    }),
                    interact.modifiers.restrict({
                        restriction: element.parentNode,
                        elementRect: { top: 0, left: 0, bottom: 1, right: 1 },
                        endOnly: true
                    })
                    ],
                    inertia: true
                })
                .on('dragmove', function (event) {
                    x += event.dx
                    y += event.dy

                    event.target.style.webkitTransform =
                    event.target.style.transform =
                        'translate(' + x + 'px, ' + y + 'px)'
                })

    }


  
      



    render() {
        return (
            <div>
                <h2>Img resizable </h2>
              
                    <img src={demoImg}  className="resizable" />


                    <div className="draggable" id="grid-snap">
                        drag user 
                    </div>

                    <div className="dragmove" style={{ width: "500px" , height :"500px" , background : "greenyellow"}}>

                        
                    <div className="draggable" id="grid-snap">
                        drag user 
                    </div>

                    </div>
                
            </div>
        );
    }
}

export default InteractJS;
