import React, { Component } from 'react';
import demoImg from "../../assets/ntr5.jpg";
// import waterMark from "../../assets/resize_logo.png";

import waterMark from "../../assets/box.jpg";

import "./index.css";
const interact = require('interactjs')



class WatermarkPosition extends Component {


    constructor(props) {
        super(props);

        this.state = {
            fontSize: 20,
            fontColor : "red",
            rotate: 20,
            textRotatation: 0,
            logoRoatation:0,
            opacity: 9,
            logoOpacity: 9,
            logoSize: 80,
            watermarkText: "© free style",
            BackgroundImgUrl: "",
            file: "",
            logoFile : "",

            // image parameters
            actualImgWidth : 1000,
            actualImgHeight  : 750
        }
    }

    componentDidMount() {


        // drag the element on page 
        const position = { x: 0, y: 0 }
        interact('.draggable-logo').draggable({
            listeners: {
                start(event) {
                    // console.log(event.type, event.target)
                },
                move(event) {
                    position.x += event.dx
                    position.y += event.dy

                    event.target.style.transform =
                        `translate(${position.x}px, ${position.y}px)`

                    console.log(event.type, event.target)
                },

            }
        })

        // resize the element 
        interact(".resizable-logo")
            .resizable({
                edges: { left: false, right: true, bottom: true, top: false },
                // modifiers: [
                //   interact.modifiers.restrictEdges({
                //     outer: "parent",
                //     endOnly: true
                //   })
                // ],
                inertia: true,
                modifiers: [
                    // keep the edges inside the parent
                    // interact.modifiers.restrictEdges({
                    //   outer: 'parent'
                    // }),

                    // minimum size
                    interact.modifiers.restrictSize({
                        min: { width: 20, height: 20 }
                    })
                ],
            })
            .on("resizemove", function (event) {
                var target = event.target
                console.log(target);
                target.style.width = event.rect.width + "px"
                target.style.height = event.rect.height + "px"
            })


              var angle = 0

            // rotate the logo 

                interact('#draggable-logo').gesturable({
                onmove: function (event) {
                    var arrow = document.getElementById('arrow')

                    angle += event.da

                    arrow.style.webkitTransform =
                    arrow.style.transform =
                    'rotate(' + angle + 'deg)'

                    document.getElementById('angle-info').textContent =
                    angle.toFixed(2) + '\u00b0'
                  }
                })

        // text drag(move)
        interact('.draggable-text').draggable({

            listeners: {
                start(event) {
                },
                move(event) {
                    position.x += event.dx
                    position.y += event.dy
                    console.log(event)
                    event.target.style.transform =
                        `translate(${position.x}px, ${position.y}px)`
                },

            }
        })

        // resize the element 
        // interact(".resizable-text")
        // .resizable({
        //         edges: { left: false, right: true, bottom: true, top: false },
        //         // modifiers: [
        //         //   interact.modifiers.restrictEdges({
        //         //     outer: "parent",
        //         //     endOnly: true
        //         //   })
        //         // ],
        //         inertia: true
        //     })
        //     .on("resizemove", function(event) {
        //         var target = event.target
        //         console.log(event);
        //         target.style.width = event.rect.width + "px"
        //         target.style.height = event.rect.height + "px"
        //     })


    }




    roateText = () =>{

        console.log("roatate text angle" , this.state.textRotatation);

        let watermarkTextEle = window.getElementById('watermarkText');
        console.log("ele" , watermarkTextEle);
        //let ele = 
        // element.setAttribute("style", "background-color: red;");
    }

    // opacityRange 

    textOpacityHandler = (e) => {
        this.setState({
            opacity : e.target.value
        })
    }

    logoOpacityHandler = (e) => {
        this.setState({
            logoOpacity: e.target.value
        })
    }

    FontHandler = (e) =>{  
        console.log(e.target.value);
        this.setState({
            fontSize : e.target.value
        })

        console.log(this.state);
    }


    handleChange = (event) => {
        console.log(event.target.files[0]);
        this.setState({
            file: URL.createObjectURL(event.target.files[0])
        })
    }

    
    handleLogoFile = (event) => {
        console.log(event.target.files[0]);
        this.setState({
            logoFile : URL.createObjectURL(event.target.files[0])
        })
    }


    handleChangeText = (e) => {
        this.setState(
            { watermarkText : e.target.value}
        )
    }

    addFont = () => {
        this.setState({
            fontSize: this.state.fontSize + 1
        })
    }

    removeFont = () => {
        this.setState({
            fontSize: this.state.fontSize - 1
        })
    }

    increaseLogo = () => {
        this.setState({
            logoSize: this.state.logoSize + 5
        })
    }

    reduceLogo = () => {

        this.setState({
            logoSize: this.state.logoSize - 5
        })
    }


    saveImg = () => {

        // let waterMarkText = document.getElementById('watermarkText');
        // console.log(waterMarkText.style.transform);

